export class BaseClickableItem {
    id!: number;
    name!: string;
    icon!: string;
    income!: number;
    power!: number;
    count!: number;

    private timer: any;

    constructor() {
        this.timer = setInterval(() => {
            this.calculateIncome();
        }, 1000);
    }

    protected addNew() {
        this.count++;
    }

    protected remove() {
        this.count--;
        if (this.count === 0) {
            this.stopTimer();
        }
    }

    private calculateIncome() { 
        return this.count * this.income;
    }

    private stopTimer() {
        clearInterval(this.timer);
    }    
}
