export class BaseClickableItem {
    id!: number;
    name!: string;
    icon!: string;
    income!: number;
    power!: number;
    count!: number;

    private timer: any;

    constructor() {
        this.timer = setInterval(() => {
            const result = this.count * this.income;
            console.log(result);
        }, 1000);
    }

    private calculateIncome() { 
        return this.count * this.income;
    }

    private stopTimer() {
        clearInterval(this.timer);
    }    
}
